// assets
function getImg() {
  return require("heatshrink").decompress(
    atob(
      ""
    )
  );
}
var IMAGEWIDTH = 176;
var IMAGEHEIGHT = 81;

Graphics.prototype.setFontMichroma36 = function () {
  g.setFontCustom(
    atob(
      ""
    ),
    46,
    atob("CBIkESMjJCMjIyMjCA=="),
    36 + (1 << 8) + (1 << 16)
  );
};

Graphics.prototype.setFontMichroma16 = function (scale) {
  g.setFontCustom(
    atob(
      ""
    ),
    46,
    atob("BAgQCBAQEBAQEBAQBA=="),
    16 + (scale << 8) + (1 << 16)
  );
};

// timer
var timervalue = 0;
var istimeron = false;
var timertick;

Bangle.on("touch", (t) => {
  if (t == 1) {
    Bangle.buzz(30);
    if (timervalue < 5 * 60) {
      timervalue = 1;
    } else {
      timervalue -= 5 * 60;
    }
  } else if (t == 2) {
    Bangle.buzz(30);
    if (!istimeron) {
      istimeron = true;
      timertick = setInterval(countDown, 1000);
    }
    timervalue += 60 * 10;
  }
});

function timeToString(duration) {
  var hrs = ~~(duration / 3600);
  var mins = ~~((duration % 3600) / 60);
  var secs = ~~duration % 60;
  var ret = "";
  if (hrs > 0) {
    ret += "" + hrs + ":" + (mins < 10 ? "0" : "");
  }
  ret += "" + mins + ":" + (secs < 10 ? "0" : "");
  ret += "" + secs;
  return ret;
}

function countDown() {
  timervalue--;

  g.reset().clearRect(0, 76, 44 + 44, g.getHeight() / 2 + 6);

  g.setFontAlign(0, -1, 0);
  g.setFont("6x8").drawString("Timer", 44, g.getHeight() / 2 - 20);
  g.setFont("Michroma16").drawString(
    timeToString(timervalue),
    44,
    g.getHeight() / 2 - 10
  );

  if (timervalue <= 0) {
    istimeron = false;
    clearInterval(timertick);

    Bangle.buzz()
      .then(() => {
        return new Promise((resolve) => setTimeout(resolve, 500));
      })
      .then(() => {
        return Bangle.buzz(1000);
      });
  } else if (timervalue <= 30 && timervalue % 10 == 0) {
    Bangle.buzz();
  }
}

function showWelcomeMessage() {
  g.reset().clearRect(0, 76, 44 + 44, g.getHeight() / 2 + 6);
  g.setFontAlign(0, 0).setFont("6x8");
  g.drawString("Touch right to", 44, 80);
  g.drawString("start timer", 44, 88);
  setTimeout(function () {
    g.reset().clearRect(0, 76, 44 + 44, g.getHeight() / 2 + 6);
  }, 8000);
}

// time
var drawTimeout;

function getGmt() {
  var d = new Date();
  var gmt = new Date(d.getTime() + d.getTimezoneOffset() * 60 * 1000);
  return gmt;
}

function getTimeFromTimezone(offset) {
  return new Date(getGmt().getTime() + offset * 60 * 60 * 1000);
}

function queueNextDraw() {
  if (drawTimeout) clearTimeout(drawTimeout);
  drawTimeout = setTimeout(function () {
    drawTimeout = undefined;
    draw();
  }, 60000 - (Date.now() % 60000));
}

function draw() {
  g.reset().clearRect(0, 24, g.getWidth(), g.getHeight() - IMAGEHEIGHT);
  g.drawImage(getImg(), 0, g.getHeight() - IMAGEHEIGHT);

  var x_sun = 176 - ((getGmt().getHours() / 24) * 176 + 4);
  g.setColor("#ff0").drawLine(
    x_sun,
    g.getHeight() - IMAGEHEIGHT,
    x_sun,
    g.getHeight()
  );
  g.reset();

  var locale = require("locale");

  var date = new Date();
  g.setFontAlign(0, 0);
  g.setFont("Michroma36").drawString(
    locale.time(date, 1),
    g.getWidth() / 2,
    46
  );
  g.setFont("6x8");
  g.drawString(locale.date(new Date(), 1), 125, 68);
  g.drawString("PAR " + locale.time(getTimeFromTimezone(1), 1), 125, 80);
  g.drawString("TYO " + locale.time(getTimeFromTimezone(9), 1), 125, 88);

  queueNextDraw();
}

// init
g.setTheme({ bg: "#fff", fg: "#000", dark: false }).clear();
draw();
Bangle.setUI("clock");
Bangle.loadWidgets();
Bangle.drawWidgets();
showWelcomeMessage();
