let bigCustomFont = [
  atob(
    ''),
  atob('Jg8dGiAaKBsoKA==')
];
let smallCustomFont = [
  atob(
    ''),
  atob(
    'BQIEBgYGBwMEBAcGAwYCBwYFBgYGBgYGBgYCAwUGBQYHBgYGBgYGBgYEBgYGCAYGBgYGBgYGBggGBgYDBgMGBgMGBgYGBgUGBgQEBgQIBgYGBgYGBQYGCAYGBgUCBQcF')
];
let interval = null;
let middleX = 120;
let middleY = 106;
let lineLength = 114;
let lineY1 = middleY - 10;
let lineY2 = middleY + 60;
let clearRectSegmentY1 = middleY - 9;
let clearRectSegmentY2 = middleY + 59;

function setBigFont () {
  g.setFontCustom(
    bigCustomFont[0],
    48,
    bigCustomFont[1],
    58
  );
}

function setSmallFont () {
  g.setFontCustom(
    smallCustomFont[0],
    32,
    smallCustomFont[1],
    12
  );
}

function drawSegment (align, base, str) {
  let point = base;
  let maxSegmentWidth = g.stringWidth('00');
  if (align === 'r') {
    point = base - g.stringWidth(str) / 2;
  }
  if (align === 'c') {
    point = base + (maxSegmentWidth / 2) -
      (g.stringWidth(str) / 2);
  }
  g.setColor(g.theme.fg);
  g.drawString(str, point, middleY - 4, false);
}

function drawDots (center) {
  g.setColor("#FA0");
  g.fillCircle(center, middleY + 10, 2);
  g.fillCircle(center, middleY + 40, 2);
}

function drawLines () {
  g.setColor("#777");
  g.drawLine(middleX - lineLength, lineY1, middleX + lineLength, lineY1);
  g.drawLine(middleX - lineLength, lineY2, middleX + lineLength, lineY2);
}

function drawDate (str) {
  let maxSegmentWidth = 236;
  g.setColor("#777");
  g.drawString(str, (maxSegmentWidth) - (g.stringWidth(str)), middleY - 22,
    false);
}

function fixedDigits (numb) {
  if (numb < 10) {
    return '0' + numb;
  }
  return numb.toString();
}

function step () {
  let d = new Date();
  let hour = d.getHours();
  let minute = d.getMinutes();
  let second = d.getSeconds();
  let day = d.getDate();
  let month = d.getMonth() + 1;
  let year = d.getFullYear();
  let dateStr = fixedDigits(day) + '.' + fixedDigits(month) + '.' + year;
  //hour = "00";
  //minute = "00";
  //second = "00";
  setBigFont();
  let drawDot1 = false;
  let drawDot2 = false;
  if (step.lastHour !== hour) {
    g.clearRect(0, clearRectSegmentY1, 240, clearRectSegmentY2);
    drawSegment('l', middleX - 120, fixedDigits(hour));
    drawDot1 = true;
    drawDot2 = true;
    step.lastMinute = null;
    step.lastSecond = null;
  }
  if (step.lastMinute !== minute) {
    g.clearRect(middleX - 41, clearRectSegmentY1, 240, clearRectSegmentY2);
    drawSegment('c', middleX - 37, fixedDigits(minute));
    drawDot1 = true;
    drawDot2 = true;
    step.lastSecond = null;
  }
  if (step.lastSecond !== second) {
    g.clearRect(middleX + 42, clearRectSegmentY1, 240, clearRectSegmentY2);
    drawSegment('r', middleX + 82, fixedDigits(second));
    drawDot2 = true;
  }
  if (drawDot1) {
    drawDots(middleX - 41);
  }
  if (drawDot2) {
    drawDots(middleX + 41);
  }
  if (step.lastDate !== dateStr) {
    g.clearRect(0, middleY - 22, 240, middleY - 12);
    setSmallFont();
    drawDate(dateStr);
  }
  step.lastDate = dateStr;
  step.lastHour = hour;
  step.lastMinute = minute;
  step.lastSecond = second;
}

function stop () {
  if (interval) {
    clearInterval(interval);
  }
}

function start () {
  if (interval) {
    clearInterval(interval);
  }
  drawLines();
  step.lastDate = null;
  step.lastHour = null;
  step.lastMinute = null;
  step.lastSecond = null;
  interval = setInterval(step, 1000);
  step();
}

start();
// Show launcher when middle button pressed
Bangle.setUI("clock");

Bangle.loadWidgets();
Bangle.drawWidgets();
Bangle.on('lcdPower', function (on) {
  if (on) {
    start();
  } else {
    stop();
  }
});
