/// /////////////////////////////////////////

let kana = katakana.KA;
let scroll = 0;

let hiramode = false;
let curkana = 'KA';
function next () {
  let found = false;
  for (const k of Object.keys(katakana).sort()) {
    if (found) {
      kana = hiramode ? hiragana[k] : katakana[k];
      curkana = k;
      return;
    }
    if (curkana === k) {
      found = true;
    }
  }
  curkana = 'KA';
  kana = hiramode ? hiragana[curkana] : katakana[curkana];
  updateWatch(ohhmm);
}

function randKana() {
  try {
    const keys = Object.keys(katakana);
    const total = keys.length;
    let index = 0 | (Math.random() * total);
    curkana = keys[index];
    kana = hiramode ? hiragana[curkana] : katakana[curkana];
  } catch (e) {
    randKana();
  }
}

function prev () {
  let oldk = '';
  let count = 0;
  for (const k of Object.keys(katakana).sort()) {
    if (curkana === k) {
      if (count > 0) {
        curkana = oldk;
        kana = katakana[curkana];
        return;
      }
    }
    oldk = k;
    count++;
  }
  curkana = oldk;
  kana = katakana[curkana];
  updateWatch(ohhmm);
}

const kanacolors = {
  A: []
};


function updateWatch (hhmm) {
  g.setFontAlign(-1, -1, 0);
  g.setBgColor(0, 0, 0);
  g.setColor(0, 0, 0);
    var whitecolor = false;
    if (curkana.indexOf('A') != -1) {
      g.setColor(1, 0, 0);
      whitecolor = true;
    } else if (curkana.indexOf('I') != -1) {
      g.setColor(0, 1, 0);
    } else if (curkana.indexOf('U') != -1) {
      g.setColor(0, 0, 1);
      whitecolor = true;
    } else if (curkana.indexOf('E') != -1) {
      g.setColor(1, 1, 0);
    } else {
      g.setColor(0, 1, 1);
    }
    g.fillRect(0, 0, w, h);

    g.setFont('Vector', 50);
    if (whitecolor) {
      g.setColor(0, 0, 0);
    } else {
      g.setColor(0.5, 0.5, 0.5);
    }
    x = 26;
    y = h - 42;
    g.drawString(hhmm, x - 3, y - 3);
    if (whitecolor) {
      g.setColor(1, 1, 1);
    } else {
      g.setColor(0, 0, 0);
    }
    g.drawString(hhmm, x, y - 1);
  
  drawKana(4 + (g.getWidth() / 6), 60);
  drawMonthDay();
  Bangle.drawWidgets();
}

function drawMonthDay() {
  g.setFont('Vector', 20);
  g.setColor(1,1,1);
  g.setFontAlign(-1, -1, 0);
  g.drawString(month, 4, 112);
  g.setFontAlign(1, -1, 0);
  g.drawString(day, w, 112);
}

function getPhoneme(k) {
  switch (k) {
    case "TU": return "TSU";
    case "TI": return "CHI";
    case "SI": return "SHI";
    case "HU": return "FU";
  }
  return k;
}
  
function drawKana (x, y) {
  g.setColor(0, 0, 0);
  g.fillRect(0, 0, g.getWidth(), 6 * (h / 8) + 1);
  g.setColor(1, 1, 1);
  g.drawImage(kana, x + 20, 40, { scale: 1.6 });
  g.setColor(1, 1, 1);
  g.setFont('Vector', 24);
  g.drawString(getPhoneme(curkana), 4, 32);
  g.drawString(hiramode ? 'H' : 'K', w - 20, 32);
}

var ohhmm = '';

function tickWatch () {
  const now = Date();
  month = now.getMonth() + 1;
  day = now.getDate();
  function zpad (n) {
    return (n < 10) ? '0' + n : n;
  }
  const hhmm = zpad(now.getHours()) + ':' + zpad(now.getMinutes());
  if (hhmm !== ohhmm) {
    randKana();
    updateWatch(hhmm);
    ohhmm = hhmm;
  }
}

Bangle.on('touch', function (tap, top) {
  if (top.x < w / 4) {
    prev();
  } else if (top.x > (w - (w / 4))) {
    next();
  } else {
    hiramode = !hiramode;
  }
  kana = hiramode ? hiragana[curkana] : katakana[curkana];
  updateWatch(ohhmm);
});

g.clear(true);
Bangle.loadWidgets();
tickWatch();
setInterval(tickWatch, 1000 * 60);

// show launcher when button pressed
Bangle.setUI('clock');

