const keys = [
  "A","I","U","E","O",
  "HA","HI","HU","HE","HO",
  "KA","KI","KU","KE","KO",
  "MA","MI","MU","ME","MO",
  "NA","NI","NU","NE","NO",
  "RA","RI","RU","RE","RO",
  "SA","SI","SU","SE","SO",
  "TA","TI","TU","TE","TO",
  "WA","WO","YO","YU","N",
 ];
let kana = katakana.KA;
// const keys = Object.keys(katakana).sort();
// console.log(keys);
let hiramode = false;
let curkana = 'KA';

console.log("StartupTime: "+startupTime.diff());

function next () {
  const off = keys.indexOf(curkana);
  if (off !== -1 && off + 1 < keys.length) {
    return keys[off + 1];
  }
  return keys[0];
}

function randKana() {
  try {
    let index = 0 | (Math.random() * keys.length);
    curkana = keys[index];
  } catch (e) {
    randKana();
  }
}
// const bench = benchStart();
// console.log("-->" + bench.diff());

function prev () {
  const off = keys.indexOf(curkana);
  if (off > 0) {
    return keys[off - 1];
  }
  return keys[keys.length - 1];
}
let color = 0;
const colors = [
  () => g.setColor(0,1,0),
  () => g.setColor(1,1,0),
  () => g.setColor(0,1,1),
  () => g.setColor(1,1,1),
  // too dark
  () => g.setColor(0,0,1),
  () => g.setColor(0,0,0),
  () => g.setColor(1,0,0),
];

function nextColor() {
  if (color + 1 >= colors.length) {
    color = 0;
  } else {
    color++;
  }
}
function prevColor() {
  if (color < 1) {
    color = colors.length - 1;
  } else {
    color--;
  }
}

function render(hhmm) {
  g.setFontAlign(-1, -1, 0);
  g.setBgColor(0, 0, 0);
  g.setColor(0, 0, 0);
    const whitecolor = color > 3;
    colors[color]();
    g.fillRect(0, 30, w, h);

    g.setFont('Vector', 50);
    if (whitecolor) {
      g.setColor(0, 0, 0);
    } else {
      g.setColor(0.5, 0.5, 0.5);
    }
    x = 26;
    y = h - 42;
    g.drawString(hhmm, x - 3, y - 3);
    if (whitecolor) {
      g.setColor(1, 1, 1);
    } else {
      g.setColor(0, 0, 0);
    }
    g.drawString(hhmm, x, y - 1);
  
  drawKana();
  drawMonthDay();
 // Bangle.drawWidgets(); // :? always draw?
}

function drawMonthDay() {
  g.setFont('Vector', 20);
  g.setColor(1,1,1);
  g.setFontAlign(-1, -1, 0);
  g.drawString(month, 4, 112);
  g.setFontAlign(1, -1, 0);
  g.drawString(day, w, 112);
}

function getPhoneme(k) {
  switch (k) {
    case "TU": return "TSU";
    case "TI": return "CHI";
    case "SI": return "SHI";
    case "HU": return "FU";
  }
  return k;
}

var ohhmm = '';
var ypos = 0;
var xpos = 0;
var zpos = 1;
function drawKana (x, y) {
  if (!x) {
    x = 4 + (g.getWidth() / 6);
  }
  if (!y) {
    y = 40;
  }
  x += xpos;
  y += ypos;
  g.setColor(0, 0, 0);
  g.fillRect(0, 30, g.getWidth(), 6 * (h / 8) + 1);
  g.setColor(1, 1, 1);
  x -= ((zpos) - 1)*50;
  y -= (zpos - 1)*50;
  kana = hiramode ? hiragana[curkana] : katakana[curkana];
  if (guard) {
  g.setColor(0.8,0.8,0.8);
  }
  g.drawImage(kana, x + 20, y, { scale: 1.6 * zpos });
  g.setColor(1, 1, 1);
  g.setFont('Vector', 24);
  g.drawString(getPhoneme(curkana), 4, 32);
  if (hiramode) {
    g.setColor(0.2,0.2,0.2)
    g.drawString('K', w - 20, 32);
    g.setColor(1, 1, 1);
    g.drawString('H', w - 20, 32+24);
  } else {
     g.setColor(1, 1, 1);
    g.drawString('K', w - 20, 32);
    g.setColor(0.2,0.2,0.2)
    g.drawString('H', w - 20, 32+24);
  }
 // g.drawString(hiramode ? 'H' : 'K', w - 20, 32);
}

function tickWatch () {
  const now = Date();
  month = now.getMonth() + 1;
  day = now.getDate();
  function zpad (n) {
    return (n < 10) ? '0' + n : n;
  }
  const hhmm = zpad(now.getHours()) + ':' + zpad(now.getMinutes());
  if (hhmm !== ohhmm) {
    ohhmm = hhmm;
    randKana();
    render(hhmm);
  }
}
let guard = false;
function hiraPush(d,dx) {
  if (guard) {
    return;
  }
  xpos = 0;
  ypos = 0;
  zpos = 1;
  guard = true;
  var count = 2;
  function paint() {
    count--;
    if (count < 0) {
      guard = false;
      xpos = 0;
      ypos = 0;
      zpos = 1;
      render(ohhmm);
      return;
    }
    zpos -= 0.04;
    render(ohhmm);
    setTimeout(paint, 100);
  }
  setTimeout (paint, 5);
}

function hiraSwipe(d,dx, dostuff) {
  if (guard) {
    return;
  }
  if (dx) {
    ypos = 0;
  } else {
    ypos = (d * 4);
  }
  xpos = 0;
  guard = true;
  var count = 2;
  function paint() {
    count--;
    if (count < 0) {
      if (dx) {
        curkana = d>0?prev():next();
      } else {
        if (dostuff) {
          hiramode = !hiramode;
        }
      }
      guard = false;
      xpos = 0;
      ypos = 0;
      render(ohhmm);
      return;
    }
    if (dx) {
      xpos += (8*d);
    } else {
      ypos -= (4*d);
    }
    render(ohhmm);
    setTimeout(paint, 5);
  }
  setTimeout (paint, 5);
}

Bangle.on('touch', function (tap, top) {
  if (top.y < (h / 1.5)) {
    if (top.x > w /2) {
    //hiramode = !hiramode;
    if (hiramode) {
      hiraSwipe(1,0, hiramode);
    } else {
      hiraSwipe(-1,0, !hiramode);
    }
    } else {
      hiraSwipe(1,1,1);
    }
  } else if (top.x < w / 2) {
    nextColor();
    hiraPush();
   // curkana = prev();
  } else {
    prevColor();
    hiraPush();
   // curkana = next();
  }
  render(ohhmm);
});
Bangle.on('swipe', function (x,y) {
  if (x > 0) {
   // nextColor();
    hiraSwipe(1, 1);
  } else if (x < 0) {
   // prevColor();
    hiraSwipe(-1,1);
  } else if (y < 0) {
    hiraSwipe(1, 0, hiramode);
  } else if (y > 0) {
    hiraSwipe(-1, 0, !hiramode);
  }
  render(ohhmm);
});

