const locale = require("locale");

let hour;
let minute;
let date;

let timer;

function getBackground() {
  return {
    width: 120,
    height: 96,
    bpp: 8,
    buffer: require("heatshrink").decompress(atob(""))
  };
}

function draw() {
  const d = new Date();

  const newHour = ('0' + d.getHours()).substr(-2);
  const newMinute = ('0' + d.getMinutes()).substr(-2);
  const newDate = locale.date(d).trim();

  g.setFontAlign(0, 0, 0);

  if (newHour !== hour) {
    g.setFont('6x8', 5);
    g.setColor(0x0000);
    g.drawString(hour, 76, 100);
    g.setColor(0xFFFF);
    g.drawString(newHour, 76, 100);
    hour = newHour;
  }

  if (newMinute !== minute) {
    g.setFont('6x8', 5);
    g.setColor(0x0000);
    g.drawString(minute, 164, 100);
    g.setColor(0xFFFF);
    g.drawString(newMinute, 164, 100);
    minute = newMinute;
  }

  if (newDate !== date) {
    g.setFont('6x8', 2);
    g.setColor(0x0000);
    g.drawString(date, 120, 228);
    g.setColor(0xFFFF);
    g.drawString(newDate, 120, 228);
    date = newDate;
  }
}

function startDrawing() {
  hour = '';
  minute = '';
  date = '';
  g.setColor(0x0000);
  g.fillRect(0, 216, 240, 240);
  g.drawImage(getBackground(), 0, 24, { scale: 2 });
  Bangle.drawWidgets();
  draw();
  timer = setInterval(draw, 1000);
}

function stopDrawing() {
  if (timer) {
    clearInterval(timer);
    timer = undefined;
  }
}

Bangle.on('lcdPower', (on) => {
  stopDrawing();
  if (on) {
    startDrawing();
  }
});

Bangle.loadWidgets();
startDrawing();

// Show launcher when button pressed
Bangle.setUI("clock");
