// DRAW FACE
let draw = function() {
  require("FontTeletext10x18Mode7").add(Graphics);
  var X = g.getWidth() / 2;
  var Y = (g.getHeight() / 2)-15;
  g.reset();
  var d = new Date();
  var clock = require("locale").time(d, 1 /*omit seconds*/);
  var meridian = require("locale").meridian(d);
  // draw the current time (4x size 7 segment)

   // align bottom right

  g.setFontBold();
  g.setFontAlign(0,1);
  g.drawString(" "+clock+" ", X-10, Y, true /*clear background*/);
  // draw the meridian(am/pm) and seconds (2x size 7 segment)
  
  g.setFont("Vector",20);

  g.setFontAlign(-1,1); // align bottom left
  g.drawString(meridian, X+50, Y-10, true /*clear background*/);
  // draw the date, in a normal font

  g.setFontAlign(0,1); // align center bottom
  // pad the date - this clears the background if the date were to change length
  var dateStr = require("locale").dow(new Date(), 1)+", "+ require("locale").month(new Date(), true)+" "+new Date().getDate();
  g.drawString(" "+dateStr+" ", g.getWidth()/2, Y+15, true /*clear background*/);
  
  
  
  // queue next draw
  if (drawTimeout) clearTimeout(drawTimeout);
  drawTimeout = setTimeout(function() {
    drawTimeout = undefined;
    draw();
  }, 60000 - (Date.now() % 60000));
};

  

  
// Show launcher when middle button pressed
Bangle.setUI({
  mode : "clock",
  remove : function() {
    // Called to unload all of the clock app
    if (drawTimeout) clearTimeout(drawTimeout);
    drawTimeout = undefined;
    delete Graphics.prototype.setFontBold;
    delete Graphics.prototype.setFontTeletext10x18Mode7;
    clockInfoMenuRight.remove();
    clockInfoMenuLeft.remove();
    
  }});
  
g.clear();
// Load widgets
Bangle.loadWidgets();
draw();
drawClockInfos();
setTimeout(Bangle.drawWidgets,0);
