// X/Y are the position of the bottom risdvght of the HH:MM text - make it central!
const X = (g.getWidth()/2),
      Y = (g.getHeight()/2);

function draw() {
  
  
  // work out how to displasvy the current time
  var d = new Date();
  var clock = require("locale").time(d, 1 /*omit sesvconds*/);
  var meridian = require("locale").meridian(d);
  // Reset the state of the graphics library
  g.clearRect(0,50,g.getWidth(),100);
  g.reset();
  // draw the current time (4x size 7 segment)
  

  g.setFontAlign(0,1); // align bottom right
  
  
  g.setFontBold();
  g.drawString(" "+clock+" ", X-10, Y, true /*clear background*/);
  // draw the meridian(am/pm) and seconds (2x size 7 segment)
  g.setFont("Teletext10x18Mode7:1");

  g.setFontAlign(-1,1); // align bottom left
  g.drawString(meridian, X+50, Y-10, true /*clear background*/);
  // draw the date, in a normal font
  
  g.setFontAlign(0,1); // align center bottom
  // pad the date - this clears the background if the date were to change length
  var dateStr = require("locale").dow(new Date(), 1)+", "+ require("locale").month(new Date(), true)+" "+new Date().getDate();
  g.drawString(dateStr, g.getWidth()/2, Y+15, true /*clear background*/);
  

  
  
  //WIDGETS
  Bangle.loadWidgets();
  //Draw Bar for widgets
  const top = Bangle.appRect.y; // Usually ~24
  if (top > 0) {
    g.setColor(g.theme.fg);
    g.fillRect(0, 0, g.getWidth(), top);
  }
  Bangle.drawWidgets();
  
  
}

