{ // must be inside our own scope here so that when we are unloaded everything disappears
  // we also define functions using 'let fn = function() {..}' for the same reason. function decls are global
let drawTimeout;
  
var bg_mario3 = {
  width : 176, height : 165, bpp : 3,
  transparent : 1,
  buffer : require("heatshrink").decompress(atob(""))
};

var bg_mario1 = {
  width : 176, height : 165, bpp : 3,
  transparent : 1,
  buffer : require("heatshrink").decompress(atob(""))
};

var bg_mario2 = {
  width : 176, height : 165, bpp : 3,
  transparent : 1,
  buffer : require("heatshrink").decompress(atob(""))
};

var bg_kirby = {
  width : 176, height : 165, bpp : 3,
  transparent : 2,
  buffer : require("heatshrink").decompress(atob(""))
};

var bg_zelda = {
  width : 176, height : 165, bpp : 8,
  transparent : 254,
  buffer : require("heatshrink").decompress(atob(""))
};
  
var settings  = Object.assign({
  currentFace : "Mario 3"
}, require("Storage").readJSON("nesclock.json", true) || {});
  
let currentFace = {};
  
if (settings.currentFace == "Mario 3") {
  currentFace = {'fontFace': "MarioBrosDS",
                 'dateFontFace': "NES",
                 'clockScale': 1,
                 'clockOffsetX': 0,
                 'clockOffsetY': 0,
                 'dateOffsetX': 0,
                 'dateOffsetY': 48,
                 'clockShadowOffsetX': 5,
                 'clockShadowOffsetY': 5,
                 'dateShadowOffsetX': 1,
                 'dateShadowOffsetY': 1,
                 'dateScale': 1,
                 'clockColor': "#4299f4",
                 'dateColor': "#ffffff",
                 'bgColor': "#f5bf66",
                 'bgImage': bg_mario3};
} else if (settings.currentFace == "Mario 2") {
  currentFace = {'fontFace': "SuperPlumberBrothers",
                 'dateFontFace': "NES",
                 'clockScale': 3,
                 'clockOffsetX': 0,
                 'clockOffsetY': -8,
                 'dateOffsetX': 0,
                 'dateOffsetY': 54,
                 'clockShadowOffsetX': 3,
                 'clockShadowOffsetY': 3,
                 'dateShadowOffsetX': 1,
                 'dateShadowOffsetY': 1,
                 'dateScale': 1,
                 'clockColor': "#ffffff",
                 'dateColor': "#ffffff",
                 'bgColor': "#9290ff",
                 'bgImage': bg_mario2};
} else if (settings.currentFace == "Mario 1") {
  currentFace = {'fontFace': "SuperPlumberBrothers",
                 'dateFontFace': "NES",
                 'clockScale': 4,
                 'clockOffsetX': 0,
                 'clockOffsetY': -8,
                 'dateOffsetX': 0,
                 'dateOffsetY': 54,
                 'clockShadowOffsetX': 3,
                 'clockShadowOffsetY': 3,
                 'dateShadowOffsetX': 1,
                 'dateShadowOffsetY': 1,
                 'dateScale': 1,
                 'clockColor': "#aa4e00",
                 'dateColor': "#ffffff",
                 'bgColor': "#9290ff",
                 'bgImage': bg_mario1};
} else if (settings.currentFace == "Kirby") {
  currentFace = {'fontFace': "KirbyClassicBig",
                 'dateFontFace': "KirbyClassic",
                 'clockScale': 1,
                 'clockOffsetX': 0,
                 'clockOffsetY': 0,
                 'dateOffsetX': 0,
                 'dateOffsetY': 54,
                 'clockShadowOffsetX': 3,
                 'clockShadowOffsetY': 3,
                 'dateShadowOffsetX': 2,
                 'dateShadowOffsetY': 2,
                 'dateScale': 1,
                 'clockColor': "#ff00ff",
                 'dateColor': "#fe6ecc",
                 'bgColor': "#ffffff",
                 'bgImage': bg_kirby};
} else if (settings.currentFace == "Zelda") {
  currentFace = {'fontFace': "Zapf",
                 'dateFontFace': "NES",
                 'clockScale': 1,
                 'clockOffsetX': 0,
                 'clockOffsetY': -10,
                 'dateOffsetX': 0,
                 'dateOffsetY': 40,
                 'clockShadowOffsetX': 3,
                 'clockShadowOffsetY': 3,
                 'dateShadowOffsetX': 2,
                 'dateShadowOffsetY': 2,
                 'dateScale': 1,
                 'clockColor': "#994e00",
                 'dateColor': "#0c9300",
                 'bgColor': "#feccc5",
                 'bgImage': bg_zelda};
}

// Actually draw the watch face
let draw = function() {
  var x = g.getWidth() / 2;
  var y = g.getHeight() / 2;
  g.reset().clearRect(Bangle.appRect); // clear whole background (w/o widgets)
  
  //Set Background color for face
  g.setColor(currentFace.bgColor);
  g.fillRect(Bangle.appRect);
  g.drawImage(currentFace.bgImage, 0, 24);
  var date = new Date();
  var timeStr = require("locale").time(date, 1); // Hour and minute
  var dateStr = require("locale").date(date, 0).toUpperCase()+"\n"+
                require("locale").dow(date, 0).toUpperCase();
  //shadows
  g.setColor(0,0,0);
  g.setFontAlign(0, 0).setFont(currentFace.fontFace, currentFace.clockScale).drawString(timeStr, x+currentFace.clockOffsetX+currentFace.clockShadowOffsetX, y+currentFace.clockOffsetY+currentFace.clockShadowOffsetY);
  g.setFontAlign(0, 0).setFont(currentFace.dateFontFace, currentFace.dateScale).drawString(dateStr, x+currentFace.dateOffsetX+currentFace.dateShadowOffsetX, y+currentFace.dateOffsetY+currentFace.dateShadowOffsetY);
  
  //main text
  g.setColor(currentFace.clockColor);
  g.setFontAlign(0, 0).setFont(currentFace.fontFace, currentFace.clockScale).drawString(timeStr, x+currentFace.clockOffsetX, y+currentFace.clockOffsetY);
  // Show date and day of week
  g.setColor(currentFace.dateColor);
  g.setFontAlign(0, 0).setFont(currentFace.dateFontFace, currentFace.dateScale).drawString(dateStr, x+currentFace.dateOffsetX, y+currentFace.dateOffsetY);


  
  // queue next draw
  if (drawTimeout) clearTimeout(drawTimeout);
  drawTimeout = setTimeout(function() {
    drawTimeout = undefined;
    draw();
  }, 60000 - (Date.now() % 60000));
};

// Show launcher when middle button pressed
Bangle.setUI({
  mode : "clock",
  remove : function() {
    // Called to unload all of the clock app
    if (drawTimeout) clearTimeout(drawTimeout);
    drawTimeout = undefined;
    currentFace = undefined;
    delete Graphics.prototype.setFontMarioBrosDS;
    delete Graphics.prototype.setFontSuperPlumberBrothers;
    delete Graphics.prototype.setFontKirbyClassic;
    delete Graphics.prototype.setFontKirbyClassicBig;
    delete Graphics.prototype.setFontZapf;
    delete Graphics.prototype.setFontNES;
  }});
// Load widgets
Bangle.loadWidgets();
draw();
setTimeout(Bangle.drawWidgets,0);
}

