Modules.addCached("Font4x5",function(){exports.add=function(a){a.prototype.setFont4x5=function(){this.setFontCustom(atob(""),32,4,5)}}});

const offset = 25;
const width = g.getWidth();
const height = g.getHeight();
const font = "Vector:12";

var drawTimeout;

var img = {
  width : 176, height : 149, bpp : 4,
  transparent : -1,
  palette : new Uint16Array([25804,806,0,21514]),
  buffer : require("heatshrink").decompress((atob("")))
};

var night= {
  width : 89, height : 76, bpp : 4,
  transparent : 2,
  buffer : (atob(""))
};

function queueDraw() {
  if (drawTimeout) clearTimeout(drawTimeout);
  drawTimeout = setTimeout(function() {
    drawTimeout = undefined;
    draw();
  }, 60000 - (Date.now() % 60000));
}

function isDark(){
   if (g.theme.dark==true){
    g.setColor(0xFFFF);
  }
  else {
    g.setColor(0x0000);
  }
}

function time() {
  var d = new Date();
  var day = d.getDate();
  var time = require("locale").time(d,1);
  var date = require("locale").date(d);
  var mo = require("date_utils").month(d.getMonth()+1,1);
  
  require("Font4x5").add(Graphics); // time
  isDark();
  g.setFontAlign(0,0);
  g.setFont("4x5",7.5).drawString(time, width/2, height/2);  

  g.setFontAlign(1,1);
  g.setFont("4x5",3).drawString(mo+" "+day, width-15, height-35);  
}

function draw() { //poketch background  
  if (g.theme.dark==true){
    g.drawImage(night, 0, 25, {scale:2}); //poketch is life  
  }
  else {
    g.drawImage(img, 0, 25); //poketch is life  
  }  
  time();
  queueDraw();
}

//program start
g.clear();
draw();

// Show launcher when middle button pressed
Bangle.setUI("clock");
// Load widgets
Bangle.loadWidgets();
Bangle.drawWidgets();
