const vw = g.getWidth();
const bgWidth = 384;

const sonic = {
  bpp: 4,
  transparent: 1,
};

const defaultSonic = {
  width: 36,
  height: 62,
  buffer: require("heatshrink").decompress(atob("")),
};

const startingBuffers = [
  {
    width: 49,
    height: 63,
    buffer: require("heatshrink").decompress(atob("")),
  },
  {
    width: 42,
    height: 62,
    buffer: require("heatshrink").decompress(atob("")),
  },
  {
    width: 34,
    height: 60,
    buffer: require("heatshrink").decompress(atob("")),
  },
  {
    width: 44,
    height: 62,
    buffer: require("heatshrink").decompress(atob("")),
  },
  {
    width: 38,
    height: 62,
    buffer: require("heatshrink").decompress(atob("")),
  },
  {
    width: 40,
    height: 60,
    buffer: require("heatshrink").decompress(atob("")),
  },
  {
    width: 36,
    height: 60,
    buffer: require("heatshrink").decompress(atob("")),
  },
  {
    width: 34,
    height: 60,
    buffer: require("heatshrink").decompress(atob("")),
  },
  {
    width: 36,
    height: 60,
    buffer: require("heatshrink").decompress(atob("")),
  },
  {
    width: 34,
    height: 60,
    buffer: require("heatshrink").decompress(atob("")),
  },
];

const stoppingBuffers = [
  {
    width: 44,
    height: 60,
    buffer: require("heatshrink").decompress(atob("")),
  },
  {
    width: 48,
    height: 60,
    buffer: require("heatshrink").decompress(atob("")),
  },
];

const waitingBuffers = [
  {
    width: 36,
    height: 62,
    offset: -1,
    buffer: require("heatshrink").decompress(atob("")),
  },
  {
    width: 38,
    height: 62,
    offset: 1,
    buffer: require("heatshrink").decompress(atob("")),
  },
];

const bg = {
  width: 384,
  height: 153,
  bpp: 8,
  transparent: 254,
  buffer: require("heatshrink").decompress(atob("")),
};

const topSpeed = 15;
const timeout = 200;
let currentSpeed = 0;
let currentSonic = -1;

let drawTimeout, drawInterval, waitTimeout;
let bgScroll = [0, null];

const fullReset = () => {
  if (drawTimeout) clearTimeout(drawTimeout);
  if (waitTimeout) clearTimeout(waitTimeout);
  if (drawInterval) clearInterval(drawInterval);
  currentSonic = -1;
  currentSpeed = 0;
};

const start = () => {
  fullReset();

  drawInterval = setInterval(() => {
    draw("start");
    bgScroll[0] += currentSpeed;
    if (bgScroll[1]) bgScroll[1] += currentSpeed;
    if (currentSpeed < topSpeed) currentSpeed++;
  }, timeout);
};

const stop = () => {
  if (drawTimeout) clearTimeout(drawTimeout);
  if (drawInterval) clearInterval(drawInterval);

  drawInterval = setInterval(() => {
    if (currentSpeed <= 0) {
      clearInterval(drawInterval);
      draw("reset");
    } else {
      draw("stop");
      bgScroll[0] += currentSpeed;
      if (bgScroll[1]) bgScroll[1] += currentSpeed;
      currentSpeed--;
    }
  }, timeout);
};

const wait = () => {
  currentSonic = -1;
  currentSpeed = 0;
  if (drawTimeout) clearTimeout(drawTimeout);
  if (drawInterval) clearInterval(drawInterval);

  drawInterval = setInterval(() => draw("wait"), timeout);

  waitTimeout = setTimeout(() => {
    clearInterval(drawInterval);
    currentSonic = -1;
    draw("reset");
  }, 7500);
};

const queueDraw = () => {
  if (drawTimeout) clearTimeout(drawTimeout);
  drawTimeout = setTimeout(function () {
    drawTimeout = undefined;
    draw("reset");
  }, 60000 - (Date.now() % 60000));
};

const drawSonic = (action) => {
  let target;

  if (action === "reset" || currentSonic === -1) {
    target = defaultSonic;
  } else if (action === "start") {
    target = startingBuffers[currentSonic];
  } else if (action === "stop") {
    if (currentSonic > 1) currentSonic = 0;
    target = stoppingBuffers[currentSonic];
  } else {
    target = waitingBuffers[currentSonic];
  }

  sonic.width = target.width;
  sonic.height = target.height;
  sonic.buffer = target.buffer;
  sonic.offset = target.offset;

  g.drawImage(
    sonic,
    vw / 2 - 30 + (50 - sonic.width) + (sonic.offset || 0),
    86 + (65 - sonic.height)
  );

  if (action === "start") {
    if (currentSonic === startingBuffers.length - 1) {
      currentSonic = 6;
    } else {
      currentSonic++;
    }
  } else if (action === "stop") {
    if (currentSpeed <= 2) {
      currentSonic = -1;
    } else if (currentSpeed <= 14) {
      currentSonic = 1;
    } else {
      currentSonic = 0;
    }
  } else {
    if (currentSonic === waitingBuffers.length - 1) {
      currentSonic = 0;
    } else {
      currentSonic++;
    }
  }
};

const drawTime = () => {
  const x = vw / 2;
  const y = 24 + 25;

  const date = new Date();
  const timeStr = require("locale").time(date, 1).trim();
  const dateStr = require("locale").date(date).toUpperCase();

  g.setColor("#000");
  g.setFontAlign(0, 0).setFont("6x8", 5);
  g.drawString(timeStr, x + 3, y + 2);

  g.setFont("6x8", 1.5);
  g.drawString(dateStr, x + 1, y + 29);

  g.setColor("#fff");
  g.setFontAlign(0, 0).setFont("6x8", 5);
  g.drawString(timeStr, x, y);

  g.setFont("6x8", 1.5);
  g.drawString(dateStr, x, y + 28);
};

const draw = (action) => {
  if (bgWidth - bgScroll[0] < 0) {
    bgScroll[0] = bgScroll[1];
    bgScroll[1] = null;
  }

  g.drawImage(bg, -bgScroll[0], 24);

  if (bgWidth - bgScroll[0] < vw) {
    bgScroll[1] = bgScroll[0] - bgWidth;
    g.drawImage(bg, -bgScroll[1], 24);
  }

  drawSonic(action);
  drawTime();
  if (action === "reset") queueDraw();
};

// Settings
const settings = require("Storage").readJSON("sonicclk-settings") || {
  activeMode: false,
  twistThreshold: 1600,
};
let isSettings = false;

const settingsMenu = {
  "": { title: "Settings" },
  "Active Mode": {
    value: settings.activeMode,
    format: (v) => (v ? "On" : "Off"),
    onchange: (v) => (settings.activeMode = v),
  },
  "Twist Thresh": {
    value: settings.twistThreshold,
    min: 800,
    max: 4000,
    step: 200,
    onchange: (v) => (settings.twistThreshold = v),
  },
  Exit: () => {
    isSettings = false;

    require("Storage").writeJSON("sonicclk-settings", settings);
    Bangle.setOptions({
      lockTimeout: 10000,
      backlightTimeout: 12000,
      twistThreshold: settings.twistThreshold,
      wakeOnTwist: !settings.activeMode,
    });

    E.showMenu();
    Bangle.setUI("clock");
    draw("reset");
    start();
  },
};

g.setTheme({ bg: "#0099ff", fg: "#fff", dark: true }).clear();

Bangle.on("lock", (locked) => {
  if (!isSettings) {
    if (locked) {
      stop();
    } else {
      start();
    }
  }
});

Bangle.on("twist", () => {
  if (settings.activeMode) {
    fullReset();
    draw("reset");
  } else {
    wait();
  }
});

Bangle.on("tap", (d) => {
  if (d.double && d.dir === "top") {
    fullReset();
    isSettings = true;
    Bangle.setLocked(false);
    E.showMenu(settingsMenu);
  }
});

Bangle.setOptions({
  lockTimeout: 10000,
  backlightTimeout: 12000,
  twistThreshold: settings.twistThreshold,
  wakeOnTwist: !settings.activeMode,
});

Bangle.setUI("clock");

Bangle.loadWidgets();
Bangle.drawWidgets();

draw("reset");

if (Bangle.isLocked()) {
  stop();
} else {
  start();
}
